import {createGlobalStyle} from 'styled-components';

export const GlobalStyle = createGlobalStyle`
@font-face {
    font-weight: bold;
    font-style: normal;
}

* {
    box-sizing: border-box;
    margin: 0;
}

body {
    font-family: 'Anthony', sans-serif;
    background-color: #red;
}

div {
    width: 100px;
    height: 100px;
}
`;
